from utils.data import load_data, add_visitfrom flask import Flask, render_template, request, redirect, url_forapp = Flask(__name__)@app.route('/')def index():    data = load_data()    sorted_shops = sorted(data.items(), key = lambda x: x[1]['visits'], reverse=True)    return render_template('index.html', shops=sorted_shops)@app.route('/add', methods=['GET', 'POST'])def add():    if request.method == 'POST':        name = request.form['shop']        note = request.form['note']        add_visit(name, note)        return redirect(url_for('index'))    return render_template('add_visit.html')        @app.route('/shop/<name>')def shop_detail(name):    data = load_data()    shop = data.get(name)    return render_template('shop_detail.html', shop=shop, name=name)    if __name__ == "__main__":    app.run(debug=True)