from PIL import Imagefile_path  = input("Enter full path for filename: ")old = Image.open(file_path)def png(image, name_input):    png_format = old.save(file_path)    image.save(f"/Users/asmaarabid/Downloads/{name_input}")def svg(image, name_input):    svg_format = old.save(file_path)    image.save(f"/Users/asmaarabid/Downloads/{name_input}")    def option_1():    print("Select the Image you want to change the format for ")    format_choice = int(input("Enter a choice (1-2): "))        if format_choice == 1:        name_input = input("Enter name for file (example: example.png): ")        print(f"You changed {file_path} to a .PNG format")        print(f"Named {name_input}")        png(old, name_input)            else:        print("Unsupported Format")        if format_choice == 2:        name_input = input("Enter name for file (example: example.svg): ")        print(f"You changed {file_path} to a .SVG format")        print(f"Named {name_input}")        png(old, name_input)            else:        print("Unsupported Format")    def option_2():    try:        width = int(input("Enter new width: "))        height = int(input("Enter new height: "))        filename = input("Enter name for resized file (e.g., resized.png): ")        re_image = old.resize((width, height))        re_image.save(f"/Users/asmaarabid/Downloads/{filename}")        print(f"Your image is now resized to {width}x{height} and saved as {filename}.")    except ValueError:        print("Please enter valid numbers for width and height.")def main_menu():    print("Welcome to Image Converter!")        print("\n1. Convert image to format")    print("2. Change image size")        while True:                choice=int(input("Enter your choice(1-2): "))                if choice ==1:            print("Enter a format \n1. .png \n.2. .svg")            option_1()            break                elif choice ==2:            option_2()            breakif __name__ == "__main__":    main_menu()