import jsonimport osfrom datetime import datetimedata_file = "/Users/asmaarabid/schoolpython/personelProjects/coffee/data/shops.json"def load_data():    if not os.path.exists(data_file):        return {}    with open(data_file, "r")as F:        return json.load(F)def save_data(data):    with open(data_file, "w") as F:        json.dump(data, F, indent=4)        def add_visit(shop_name, note=""):    data = load_data()    if shop_name not in data:        data[shop_name] = {"visits": 0, "last_visit": "", "notes": []}            data[shop_name]["visits"] += 1    data[shop_name]["last_visit"] = datetime.today().strftime("%d-%m-%Y")    if note:        data[shop_name]["notes"].append(note)            save_data(data)    print(f"Saved for {shop_name}")    